import React from 'react';

import OpenWeatherMap from '../APIs/OpenWeatherMap';
import Widget from './Widget';
import Editor from './Editor';


class App extends React.Component {
    state = { temperature: null, errorMessage: null, unit: 'metric' };

    componentDidMount() {
        window.navigator.geolocation.getCurrentPosition(
            (position) => this.getTemperature(position),
            (err) => this.setState({ errorMessage: err.message })
        );
    }

    getTemperature = async (position) => {
        const longitude = Math.round(position.coords.longitude);
        const latitude = Math.round(position.coords.latitude);

        const response = await OpenWeatherMap.get('/weather', {
            params: {
                lat: latitude,
                lon: longitude,
                unit: this.state.unit
            }
        });
        console.log(response);
        this.setState({
            temperature: response.data.main.temp
        });
    }

    // getSeason = async (th) => {
    //     const response = await youtube.get('/search', {
    //         params: {
    //             q: term
    //         }
    //     });

    //     this.setState({
    //         videos: response.data.items,
    //         selectedVideo: response.data.items[0]
    //     });
    // }
    render() {
        return (
            <div className="ui container">
                <div className="ui grid">
                    <div className="ui row">
                        <div className="eight wide column">
                            <Editor />
                        </div>
                        <div className="eight wide column">
                            <Widget
                                temperature={this.state.temperature}
                            />
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default App;
