import React from 'react';

import OpenWeatherMap from '../APIs/OpenWeatherMap';
import Widget from './Widget';
import Editor from './Editor';

class App extends React.Component {

    geoData = {};

    state = {
        temperature: null,
        errorMessage: null,
        unit: 'metric',
        city: '',
        title: '',
        showWind: true,
        windSpeed: 0,
        windDirection: ''
    };

    toggleWindDisplay = (bool) => {
        this.setState({
            showWind: bool
        });
    }

    handleUnitChange = () => {
        const newUnit = this.state.unit === 'metric' ? 'imperial' : 'metric';

        this.setState(
            {
                unit: newUnit
            },
            () => this.getWeather(this.geoData));
    }

    componentDidMount() {
        window.navigator.geolocation.getCurrentPosition(
            (position) => {
                this.geoData = position;
                this.getWeather(position);
            },
            (err) => this.setState({ errorMessage: err.message })
        );
    }

    getWeather = async (position) => {
        const longitude = position.coords.longitude;
        const latitude = position.coords.latitude;
        const response = await OpenWeatherMap.get('/weather', {
            params: {
                lat: latitude,
                lon: longitude,
                units: this.state.unit
            }
        });

        this.setState({
            temperature: response.data.main.temp,
            windSpeed: response.data.wind.speed,
            windDirection: response.data.deg
        });
    }

    onTitleInputChange = (input) => {
        this.setState({ title: input });
    }

    render() {
        return (
            <div className="ui container">
                <table className="ui two column table">
                    <tbody>
                        <tr>
                            <td>
                                <Editor
                                    titleBar={this.state.title}
                                    onTitleInputChange={this.onTitleInputChange}
                                    handleUnitChange={this.handleUnitChange}
                                    toggleWindDisplay={this.toggleWindDisplay}
                                />
                            </td>
                            <td>
                                <Widget
                                    title={this.state.title}
                                    temperature={this.state.temperature}
                                    windSpeed={this.state.showWind ? this.state.windSpeed : null}
                                />
                            </td>
                        </tr>
                    </tbody>
                </table >
            </div>
        );
    }
}

export default App;
