import React from 'react';

import WeatherImage from './WeatherImage';
import '../CSS/Widget.css';

const Widget = (props) => {

    const windInfo = props.windDirection + ' ' + props.windSpeed;
    return (
        <div className="ui left aligned padded grid" >
            <div className="sixteen wide column item">
                <h5 id="widget-title">{props.title}</h5>
            </div>
            <div className="sixteen column row ">
                <div className="six wide column item">
                    <WeatherImage
                        imgID={props.imgID}
                        imgDescription={props.imgDescription}
                    />
                </div>
                <div className="ten wide column item">
                    {props.city}
                    <h1 id="temperature">{props.temperature}</h1>
                    {
                        props.showWind &&
                        <div><h5>Wind  </h5>{windInfo}</div>
                    }
                </div>
            </div>
        </div >
    );
};

export default Widget;
