import React from 'react';

const TitleBar = ({ title }) => {

    state = { term: '' }

    onInputChange = (event) => {
        this.setState({ term: event.target.value });
    }

    return (
        <div>
            <div className="ui row">
                <div className="ui input">
                    <input
                        type="text"
                        value={title}
                        placeholder="Title of Widget"
                        onInput
                    />
                </div>
            </div >
        </div>
    );
};

export default TitleBar;
