import React from 'react';

class WindSwitch extends React.Component {

    state = { showWind: true };

    handleClick = () => {
        this.setState({
            showWind: !this.state.showWind
        },
            () => this.props.toggleWindSwitch(this.state.showWind));

    }

    render() {
        return (
            <td>
                <table className="ui two very basic column table">
                    <tbody>
                        <tr>
                            <td>
                                <input
                                    type="radio"
                                    checked={this.state.showWind}
                                    onClick={this.handleClick}
                                />
                                <label>'C</label>
                            </td >
                            <td>
                                <input
                                    type="radio"
                                    checked={!this.state.showWind}
                                    onClick={this.handleClick}
                                />
                                <label>'F</label>
                            </td>
                        </tr>
                    </tbody>
                </table >
            </td>
        );
    }
};

export default WindSwitch;
